package Area_rea.java;

public class Reactanglearea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float length = 10.5f;
		float breadth = 5.2f;
		float area;
		area=length*breadth;
		
		System.out.println("area of reactangle using float::"+area);
		
		
		double l =12.3;
		double b =11.3;
		double area1;
		
		area1=l*b;
		System.out.println("area of reactangle using double:."+area1);
		

	}

}
